<script>
import { Pages } from '@/enums/Pages';

export default {
	name: 'MainMenu',
	props: {
		page: {
			type: Pages,
			required: true
		}
	},
	methods: {
		changePage(page){
			this.$emit('changePage', page);
		},
		selectCalculator(){
			this.changePage(Pages.CALCULATOR);
		},
		selectSetup(){
			this.changePage(Pages.SETUP);
		}
	},
	computed: {
    isCalculatorSelected() {
      return this.page === Pages.CALCULATOR;
    },
    isSetupSelected() {
      return this.page === Pages.SETUP;
    }
  }
}
</script>

